import React, {useState, useEffect} from 'react';
import {navigate, Link} from '@reach/router';
import PetForm from './PetForm';
import axios from 'axios';

const UpdatePet = props => {

  const [pet, setPet] = useState({});
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    grabSpecificPet();
  }, [])

  const grabSpecificPet = () => {
    console.log(props._id);
    axios.get(`http://localhost:8000/api/pets/${props._id}`)
    .then(res => {
      setPet(res.data);
      setLoaded(true);
    }).catch(err => console.log(err));
  }


  const updatePet = thepet => {
    axios.put(`http://localhost:8000/api/pets/${props._id}`, thepet)
      .then( res => {
        console.log(res);
        navigate("/")
      }).catch(err => console.log(err));
  }

  return (
    <>
    {loaded && (
      <PetForm onSubmitProp={updatePet}
      initName={pet.name} initType={pet.type} initDesc={pet.description}
      initSkill1={pet.skill1} initSkill2={pet.skill2} initSkill3={pet.skill3}
      subtitle={`Edit ${pet.name}`} submitMessage="Edit Pet" />
    )}
    </>
  )
}

export default UpdatePet;
