import React, {useEffect, useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Link, Router, navigate} from '@reach/router';
import PetList from "./components/PetList";
import PetForm from "./components/PetForm";
import UpdatePet from "./components/UpdatePet";
import Details from "./components/Details";
import axios from 'axios';

function App() {

  const [errors, setErrors] = useState({});

  const createPet = pet => {
    axios.post("http://localhost:8000/api/pets", pet)
      .then( res => {
        if(res.data.errors){
          setErrors(res.data.errors);
        } else{
          navigate("/");
        }
      }).catch(err => console.log(err));
  }

  return (
    <div className="container">
      <Router >
        <PetList path="/" />
        <PetForm path="/new" onSubmitProp={createPet}
          initName="" initType="" initDesc="" 
          initSkill1="" initSkill2="" initSkill3=""
          submitMessage="Add Pet" subtitle="Know a pet needing a home?"/>
        <Details path="/:_id"/>
        <UpdatePet path="/:_id/edit"/>
      </Router>
    </div>
  );
}

export default App;
